mapper.select:

select 

from jin_ledger
where 1=1

      and status = #{status,jdbcType=TINYINT}


    and ledgerId = #{ledgerId,jdbcType=VARCHAR}


    and tuzhibianhao like concat('%',#{tuzhibianhao,jdbcType=VARCHAR},'%')


    and tuzhimingcheng like concat('%',#{tuzhimingcheng,jdbcType=VARCHAR},'%')


   and xingzhuang = #{xingzhuang,jdbcType=VARCHAR}


    and cailiao = #{cailiao,jdbcType=TINYINT}



  and shuliang = #{shuliang,jdbcType=INTEGER}


  and danjia = #{danjia,jdbcType=VARCHAR}



   and create_time >= #{createTime_start,jdbcType=TIMESTAMP}



   and create_time < #{createTime_end,jdbcType=TIMESTAMP}


  order by  create_time desc
